import React, { useLayoutEffect } from 'react'
import { StatusBar } from 'react-native'
import { StyleSheet, Text, View } from 'react-native'
import { KeyboardAvoidingView } from 'react-native';
import { Button } from 'react-native-elements'



const ExitingScreen4 = ({ navigation }) => {
    const switchDir = () => {
        navigation.replace('Exited')
    }

    useLayoutEffect(() => {
        navigation.setOptions({
            title: "Exiting Directions",
            headerStyle: { backgroundColor: "#fff" },
            headerTitleStyle: { color: "black"},
            headerTintColor: "black",
        })
    }, [navigation])
    
    return (
        <KeyboardAvoidingView behavior='padding' style={styles.container}>
            <StatusBar style="light" /> 
            <Text><h2>EXIT</h2></Text>

            <Text><h3>Move straight ahead towards EXIT.</h3></Text>
            <Button containerStyle={styles.button} onPress={switchDir} type="outlined"/>

        </KeyboardAvoidingView>
    )
}

export default ExitingScreen4

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: 'white', 
        alignItems: 'center',
        justifyContent: 'center',
        padding: 10,
    },
    inputContainer: {
        width: 300,
    },
    button: {
        width: 200,
        height: 100,
        marginTop: 200,
    },
})
