import React, { useLayoutEffect, useState, useEffect } from 'react'
import { ScrollView, SafeAreaView, StyleSheet, Text, View, TouchableOpacity } from 'react-native'
import { Avatar, Image} from 'react-native-elements'
import { AntDesign, SimpleLineIcons } from '@expo/vector-icons'
import { auth, database, db } from '../firebase'
import CustomListItem from '../components/CustomListItem'
import { Button } from 'react-native'
import { KeyboardAvoidingView } from 'react-native';


const HomeScreen = ({ navigation }) => {

    const [chats, setChats] = useState([])

    const signOutUser = () => {
        auth.signOut().then(() => {
            navigation.replace('Login')
        })
    }

    useEffect(() => {
        const unsubscribe = db.collection('data').onSnapshot(snapshot => (
            setChats(snapshot.docs.map(doc => ({
                id: doc.id,
                data: doc.data(),
            })))
        ))

        return unsubscribe; 
    }, [])

    useLayoutEffect(() => {
        navigation.setOptions({
            title: "Welcome username1!",
            headerStyle: { backgroundColor: "#fff" },
            headerTitleStyle: { color: "black"},
            headerTintColor: "black",
            headerLeft: () => (
                <View style={{marginLeft: 15}}>
                    <TouchableOpacity onPress={signOutUser} activeOpacity={0.5}>
                        <Avatar rounded source={{ uri: auth?.currentUser?.photoURL}}/>
                    </TouchableOpacity>
                </View>
            )
        })
    }, [navigation])

    const enterChat = (id, chatName) => {
        navigation.navigate('Chat', {
            id: id,
            chatName: chatName,
        })
    }
    database.ref("Users/123456/carstatus").get().then(function(snapshot) {
        if (snapshot.exists()) {
            console.log(snapshot.val());
        }
        else {
          console.log("No data available");
        }
      }).catch(function(error) {
        console.error(error);
      });

      const bookSlot = () => {
        database.ref('Users/username1' ).set({
            carstatus: 'booked',
            carnumber: "ABC123"
      })
    }

    return (
        <KeyboardAvoidingView behavior='padding' style={styles.container}>
            <StatusBar style="light" /> 
            <Image 
                source={{
                    uri: "https://i.pinimg.com/originals/cd/9d/a2/cd9da2bdbdcc280b6702239df7837d1e.png",
                }}  
                style={{width: 200, height: 200}}
            />
            <View style={styles.inputContainer}>
                <Input 
                    placeholder="Email Address" 
                    autoFocus 
                    type="email" 
                    value={email} 
                    onChangeText={text => setEmail(text)}
                />
                <Input 
                    placeholder="Password" 
                    secureTextEntry 
                    type="password"
                    value={password} 
                    onChangeText={text => setPassword(text)}
                    onSubmitEditing={SignIn}
                />
            </View>
            <Button raised containerStyle={styles.button} onPress={bookSlot} title="Book a Slot"/>
            <View style={{height: 100}}/>

        </KeyboardAvoidingView>
    )
}

export default HomeScreen

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: 'white', 
        alignItems: 'center',
        justifyContent: 'center',
        padding: 10,
    },
    inputContainer: {
        width: 300,
    },
    button: {
        width: 200,
        marginTop: 10,
    },
})
